import React, { useEffect, useState } from 'react';
import jsPDF from 'jspdf';
import API from '../utils/api';

function Report({ user }) {
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchReport();
  }, []);

  const fetchReport = async () => {
    try {
      const res = await API.get('/reports');
      if (res.data && res.data.length > 0) {
        setReport(res.data[0]);
      }
    } catch (err) {
      console.error("Error fetching report", err);
    }
  };

  const generateReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await API.post('/reports/generate');
      setReport(res.data);
    } catch (err) {
      console.error("Error generating report", err);
      setError("Failed to generate report. Please ensure you have completed all 5 quizzes.");
    } finally {
      setLoading(false);
    }
  };

  const generatePDF = () => {
    if (!report) return;
    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text('Margdarshak Career Report', 10, 20);

    doc.setFontSize(16);
    doc.text('Recommended Careers:', 10, 40);

    let y = 50;
    report.careers.forEach((career, index) => {
      doc.setFontSize(14);
      doc.text(`${index + 1}. ${career}`, 15, y);
      y += 10;
    });

    doc.setFontSize(16);
    doc.text('Roadmap:', 10, y + 20);
    y += 30;

    const roadmapLines = doc.splitTextToSize(JSON.stringify(report.roadmap, null, 2), 180);
    doc.setFontSize(12);
    doc.text(roadmapLines, 15, y);

    doc.save('Margdarshak_Report.pdf');
  };

  if (loading) return <div className="section text-center"><h2>Generating your personalized report...</h2></div>;

  if (!report) {
    return (
      <div className="section text-center">
        <h2>Ready to see your future?</h2>
        <p>Complete all 5 quizzes to unlock your personalized career report.</p>
        {error && <div style={{ color: 'red', marginBottom: '1rem' }}>{error}</div>}
        <button className="btn" onClick={generateReport}>Generate Report</button>
      </div>
    );
  }

  return (
    <div className="section">
      <div className="text-center mb-2">
        <h2>Your Career Report</h2>
        <p>Based on your unique profile, here are our top recommendations.</p>
      </div>

      <div className="report-section">
        <h3>Recommended Careers</h3>
        <div className="grid">
          {report.careers && report.careers.map((career, index) => (
            <div key={index} className="card career-card">
              <h4>{career}</h4>
            </div>
          ))}
        </div>
      </div>

      <div className="report-section">
        <h3>Your Personalized Roadmap</h3>
        <pre style={{ whiteSpace: 'pre-wrap', fontFamily: 'inherit', background: '#f8f9fa', padding: '1rem', borderRadius: '8px' }}>
          {typeof report.roadmap === 'string' ? report.roadmap : JSON.stringify(report.roadmap, null, 2)}
        </pre>
      </div>

      <div className="text-center">
        <button className="btn" onClick={generatePDF} style={{ marginRight: '1rem' }}>Download PDF</button>
        <button className="btn btn-secondary">Talk to AI Mentor</button>
      </div>
    </div>
  );
}

export default Report;
