import React, { useEffect, useState } from 'react';
import jsPDF from 'jspdf';
import API from '../utils/api';

function Report({ user }) {
  const [report, setReport] = useState(null);

  useEffect(() => {
    API.get('/reports').then(res => setReport(res.data[0]));
  }, []);

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text('Career Report', 10, 10);
    doc.save('report.pdf');
  };

  if (!report) return <div className="section">No report yet. <button onClick={() => API.post('/reports/generate')}>Generate</button></div>;

  return (
    <div className="section">
      <h2>Your Career Report</h2>
      <p>Careers: {JSON.stringify(report.careers)}</p>
      <p>Roadmap: {JSON.stringify(report.roadmap)}</p>
      <button className="btn" onClick={generatePDF}>Download PDF</button>
      <button className="btn">Talk to AI Mentor</button>
    </div>
  );
}

export default Report;
