import React, { useEffect, useState } from "react";
import jsPDF from "jspdf";
import API from "../utils/api";

export default function Report() {
  const [reports, setReports] = useState([]);
  const [selected, setSelected] = useState(null);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    loadReports();
  }, [page]);

  const loadReports = async () => {
    const res = await API.get(`/reports?page=${page}&limit=5`);
    setReports(res.data.data || []);
    setTotalPages(res.data.totalPages || 1);
  };

  const generateReport = async () => {
    setLoading(true);
    try {
      const res = await API.post("/reports/generate");
      setSelected(res.data);
      loadReports();
    } catch {
      alert("Complete all quizzes first!");
    }
    setLoading(false);
  };

  const generatePDF = (report) => {
    const doc = new jsPDF();
    doc.text(report.title, 10, 10);

    let y = 20;
    report.careers.forEach((c, i) => {
      doc.text(`${i + 1}. ${c.career}`, 10, y);
      y += 6;
      doc.text(`Why: ${c.reason}`, 10, y);
      y += 8;
      c.roadmap.forEach(step => {
        doc.text(`• ${step}`, 12, y);
        y += 6;
      });
      y += 6;
    });

    doc.save("Career_Report.pdf");
  };

  if (selected) {
    return (
      <div className="section">
        <button className="btn" onClick={() => setSelected(null)}>← Back</button>
        <h2>{selected.title}</h2>

        {selected.careers.map((c, i) => (
          <div key={i} className="card" style={{ marginBottom: "1rem" }}>
            <h3>{c.career}</h3>
            <p><strong>Why:</strong> {c.reason}</p>
            <h4>Roadmap:</h4>
            <ul>
              {c.roadmap.map((step, idx) => (
                <li key={idx}>{step}</li>
              ))}
            </ul>
          </div>
        ))}

        <button className="btn" onClick={() => generatePDF(selected)}>
          Download Report PDF
        </button>
      </div>
    );
  }

  return (
    <div className="section">
      <h2>Your Career Reports</h2>
      <button className="btn" onClick={generateReport}>
        {loading ? "Generating..." : "Generate Report"}
      </button>

      <div className="grid" style={{ marginTop: "2rem" }}>
        {reports.map(r => (
          <div key={r._id} className="card">
            <h3>{r.title}</h3>
            <p>{new Date(r.createdAt).toLocaleDateString()}</p>
            <button className="btn" onClick={() => setSelected(r)}>View</button>
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="text-center mt-2">
          <button
            className="btn"
            disabled={page === 1}
            onClick={() => setPage(page - 1)}
          >Prev</button>

          <span> Page {page} of {totalPages} </span>

          <button
            className="btn"
            disabled={page === totalPages}
            onClick={() => setPage(page + 1)}
          >Next</button>
        </div>
      )}
    </div>
  );
}
