import React, { useEffect, useState } from 'react';
import API from '../utils/api';

function Resources() {
    const [resources, setResources] = useState([]);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [search, setSearch] = useState('');
    const [type, setType] = useState('');
    const [category, setCategory] = useState('');

    useEffect(() => {
        fetchResources();
    }, [page, search, type, category]);

    const fetchResources = () => {
        let query = `/resources?page=${page}&limit=6`;
        if (search) query += `&search=${search}`;
        if (type) query += `&type=${type}`;
        if (category) query += `&category=${category}`;

        API.get(query)
            .then(res => {
                setResources(res.data.data);
                setTotalPages(res.data.totalPages);
            })
            .catch(err => console.error(err));
    };

    const handleSearchChange = (e) => {
        setSearch(e.target.value);
        setPage(1); // Reset to page 1 on search
    };

    const handleTypeChange = (e) => {
        setType(e.target.value);
        setPage(1);
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value);
        setPage(1);
    };

    return (
        <div className="section">
            <h2 className="text-center mb-2">Learning Resources</h2>

            <div className="search-container">
                <input
                    type="text"
                    className="search-input"
                    placeholder="Search resources..."
                    value={search}
                    onChange={handleSearchChange}
                />
                <select value={type} onChange={handleTypeChange} className="filter-select">
                    <option value="">All Types</option>
                    <option value="Video">Video</option>
                    <option value="Article">Article</option>
                    <option value="PDF">PDF</option>
                    <option value="Tool">Tool</option>
                </select>
                <input
                    type="text"
                    className="search-input"
                    placeholder="Category (e.g. Science)"
                    value={category}
                    onChange={handleCategoryChange}
                />
            </div>

            <div className="grid">
                {resources.map((res) => (
                    <div key={res._id} className="card">
                        <span className="badge" style={{ background: 'var(--secondary)', color: '#fff' }}>{res.type}</span>
                        <h4 style={{ marginTop: '0.5rem' }}>{res.title}</h4>
                        <p style={{ fontSize: '0.9rem', color: '#666' }}>{res.category}</p>
                        <p className="mb-1">{res.description}</p>
                        <a href={res.link} target="_blank" rel="noopener noreferrer" className="btn btn-sm">View Resource</a>
                    </div>
                ))}
            </div>

            {totalPages > 1 && (
                <div className="text-center mt-2">
                    <button
                        className="btn"
                        disabled={page === 1}
                        onClick={() => setPage(page - 1)}
                        style={{ marginRight: '10px' }}
                    >
                        Prev
                    </button>
                    <span>Page {page} of {totalPages}</span>
                    <button
                        className="btn"
                        disabled={page === totalPages}
                        onClick={() => setPage(page + 1)}
                        style={{ marginLeft: '10px' }}
                    >
                        Next
                    </button>
                </div>
            )}
        </div>
    );
}

export default Resources;
