import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../utils/api';

function Dashboard({ user }) {
  const [quizzes, setQuizzes] = useState([]);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    API.get('/quizzes').then(res => {
      setQuizzes(res.data);
      setProgress(res.data.filter(q => q.completed).length / 5 * 100);
    });
  }, []);

  return (
    <div className="section">
      <h2>Welcome, {user.name}!</h2>
      <p>Progress: {progress}%</p>
      <div className="grid">
        {['orientation', 'interest', 'aptitude', 'personality', 'emotional'].map(type => (
          <div key={type} className="card">
            <h3>{type.charAt(0).toUpperCase() + type.slice(1)} Test</h3>
            <Link to={`/quiz/${type}`}><button className="btn">Start</button></Link>
          </div>
        ))}
      </div>
      {progress === 100 && <Link to="/report"><button className="btn">Generate Report</button></Link>}
    </div>
  );
}

export default Dashboard;
