import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../utils/api';

function Quiz({ user }) {
  const { type } = useParams();
  const navigate = useNavigate();
  const [responses, setResponses] = useState({});

  const questions = [
    { id: 1, text: 'Sample question 1?', options: ['A', 'B'] },
    { id: 2, text: 'Sample question 2?', options: ['A', 'B'] },
  ];

  const handleSubmit = async () => {
    await API.post('/quizzes', { type, responses });
    navigate('/dashboard');
  };

  return (
    <div className="section">
      <h2>{type} Quiz</h2>
      {questions.map(q => (
        <div key={q.id}>
          <p>{q.text}</p>
          {q.options.map(opt => (
            <label key={opt}>
              <input type="radio" name={q.id} value={opt} onChange={(e) => setResponses({ ...responses, [q.id]: e.target.value })} />
              {opt}
            </label>
          ))}
        </div>
      ))}
      <button className="btn" onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default Quiz;
